mapper.select:


    is_active = 1

        AND question_type = #{type}


        AND difficulty = #{difficulty}


        AND content LIKE CONCAT('%', #{keyword}, '%')


        AND question_id IN (
            SELECT DISTINCT question_id FROM question_tag
            WHERE tag_id IN

                #{tagId}

        )


ORDER BY question_id DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(1) FROM question

    is_active = 1

        AND question_type = #{type}


        AND difficulty = #{difficulty}


        AND content LIKE CONCAT('%', #{keyword}, '%')


        AND question_id IN (
            SELECT DISTINCT question_id FROM question_tag
            WHERE tag_id IN

                #{tagId}

        )


WHERE is_active = 1 AND question_type = #{type}
ORDER BY RAND()
LIMIT #{limit}
